export type FlexAlignUnion =
  | "baseline"
  | "center"
  | "flex-end"
  | "flex-start"
  | "stretch"
  | "inherit"
  | "initial"
  | "unset";

export type FlexJustifyUnion =
  | "baseline"
  | "center"
  | "end"
  | "first baseline"
  | "flex-end"
  | "flex-start"
  | "last baseline"
  | "left"
  | "right"
  | "safe"
  | "space-around"
  | "space-between";

export type FlexDirection =
  | "row"
  | "column"
  | "column-reverse"
  | "row-reverse"
  | "inherit"
  | "initial"
  | "unset";
